.pushdown-list ul li:last-child {
  border-bottom: none !important;
}
.pushdown-list ul li a {
  color: var(--kv-charcoal) !important;
  font-size: 0.95em !important;
  font-weight: 500 !important;
  text-decoration: none !important;
  padding: 14px 20px !important;
  margin: 0 !important;
  display: block !important;
  background: none !important;
  background-color: transparent !important;
  border: none !important;
  border-left: 3px solid transparent !important;
  border-radius: 0 !important;
  box-shadow: none !important;
  transition: all 0.2s ease !important;
  text-align: left !important;
  width: 100% !important;
  box-sizing: border-box !important;
}
.pushdown-list ul li a:hover {
  background-color: rgba(245, 166, 35, 0.08) !important;
  color: var(--kv-gold-dark) !important;
  border-left-color: var(--kv-gold-primary) !important;
  padding-left: 24px !important;
  border-radius: 0 !important;
}
/* Dropdown Arrow Indicator */
.pushdown > a.pushdown {
  cursor: pointer !important;
  padding: 5px 10px !important;
  margin-left: 5px !important;
  font-size: 0.8em !important;
  color: var(--kv-charcoal) !important;
}
/* ========================================
   HORIZONTAL NAVIGATION BAR
   ======================================== */
.horizontal-nav {
  background: linear-gradient(135deg, var(--kv-gold-primary) 0%, var(--kv-orange-accent) 100%) !important;
  padding: 20px !important;
  color: var(--kv-white) !important;
  border-bottom: none !important;
}
.horizontal-nav h2 {
  color: var(--kv-white) !important;
  font-size: 1.8em !important;
  font-weight: 600 !important;
  margin: 0 !important;
  text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.1);
}
/* ========================================
   MAIN CONTENT AREA
   ======================================== */
#main {
  background-color: var(--kv-warm-white) !important;
}
/* Hero Section with Gradient */
.row.amethyst.no-toc {
  background: linear-gradient(135deg, var(--kv-gold-primary) 0%, var(--kv-orange-accent) 100%) !important;
  color: var(--kv-white) !important;
  padding: 60px 20px !important;
}
.row.amethyst.no-toc h1 {
  color: var(--kv-white) !important;
  font-size: 2.5em !important;
  font-weight: 700 !important;
  margin-bottom: 15px !important;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.2);
}
.row.amethyst.no-toc .subheader {
  color: var(--kv-white) !important;
  font-size: 1.2em !important;
  font-weight: 300 !important;
  line-height: 1.6 !important;
}
.row.amethyst.no-toc .anchor-list li a {
  color: var(--kv-white) !important;
  border: 2px solid var(--kv-white) !important;
  padding: 10px 20px !important;
  border-radius: 30px !important;
  font-weight: 600 !important;
  text-decoration: none !important;
  transition: all 0.3s ease !important;
}
.row.amethyst.no-toc .anchor-list li a:hover {
  background-color: var(--kv-white) !important;
  color: var(--kv-gold-primary) !important;
}
/* ========================================
   SECTION STYLING
   ======================================== */
/* Gray Section */
.row.portal.gray {
  background-color: var(--kv-warm-white) !important;
  padding: 60px 20px !important;
}
.row.portal.gray h2 {
  color: var(--kv-charcoal) !important;
  font-size: 2em !important;
  margin-bottom: 15px !important;
  border-left: 5px solid var(--kv-gold-primary) !important;
  padding-left: 15px !important;
}
.row.portal.gray .subheader {
  color: var(--kv-text-secondary) !important;
  font-size: 1.1em !important;
  line-height: 1.6 !important;
  margin-bottom: 30px !important;
}
/* Snow Section */
.row.portal.snow {
  background-color: var(--kv-white) !important;
  padding: 60px 20px !important;
}
.row.portal.snow h2 {
  color: var(--kv-charcoal) !important;
  font-size: 2em !important;
  margin-bottom: 15px !important;
  border-left: 5px solid var(--kv-orange-accent) !important;
  padding-left: 15px !important;
}
/* ========================================
   CARD STYLING
   ======================================== */
.card {
  background-color: var(--kv-white) !important;
  border: 1px solid var(--kv-grey-light) !important;
  border-radius: 10px !important;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.08) !important;
  margin-bottom: 25px !important;
  transition: transform 0.3s ease, box-shadow 0.3s ease !important;
}
.card:hover {
  transform: translateY(-5px) !important;
  box-shadow: 0 8px 20px rgba(245, 166, 35, 0.3) !important;
}
/* Card Variants */
.card.snow {
  background-color: var(--kv-warm-white) !important;
  border-left: 4px solid var(--kv-gold-primary) !important;
}
.card.gray {
  background-color: var(--kv-grey-light) !important;
  border-left: 4px solid var(--kv-orange-accent) !important;
}
/* Title Styling in Cards */
.title-snow-bg,
.title-grey-bg {
  background: linear-gradient(90deg, var(--kv-light-bg) 0%, transparent 100%) !important;
  border-left: 5px solid var(--kv-gold-primary) !important;
  padding: 15px !important;
  margin-bottom: 20px !important;
}
.title-snow-bg h3,
.title-grey-bg h3 {
  color: var(--kv-charcoal) !important;
  font-size: 1.5em !important;
  font-weight: 600 !important;
  margin: 0 !important;
}
/* ========================================
   DESCRIPTION LISTS - ALIGNMENT FIX
   ======================================== */
/* Description List Container */
.c-description-list {
  display: grid !important;
  grid-template-columns: 1fr !important;
  gap: 15px !important;
  width: 100% !important;
}
/* Description List Group */
.c-description-list__group {
  display: grid !important;
  grid-template-columns: 200px 1fr !important;
  gap: 20px !important;
  align-items: start !important;
  padding: 10px 0 !important;
  border-bottom: 1px solid var(--kv-grey-light) !important;
}
.c-description-list__group:last-child,
.c-description-list__group.noline {
  border-bottom: none !important;
}
/* Term (Label) */
.c-description-list__term {
  color: var(--kv-text-secondary) !important;
  font-weight: 600 !important;
  font-size: 0.95em !important;
  text-align: left !important;
  padding-right: 10px !important;
}
/* Detail (Value) */
.c-description-list__detail {
  color: var(--kv-text-primary) !important;
  font-size: 0.95em !important;
  word-wrap: break-word !important;
  overflow-wrap: break-word !important;
}
.c-description-list__detail .c-description-list__term {
  font-weight: 400 !important;
}
/* For horizontal layout variant */
.c-description-list--horizontal .c-description-list__group {
  grid-template-columns: 180px 1fr !important;
}
/* For short terms variant */
.c-description-list--terms-short .c-description-list__group {
  grid-template-columns: 150px 1fr !important;
}
/* ========================================
   BUTTONS & LINKS
   ======================================== */
a {
  color: var(--kv-gold-primary) !important;
  transition: color 0.2s ease !important;
}
a:hover {
  color: var(--kv-gold-dark) !important;
}
.button {
  background-color: var(--kv-gold-primary) !important;
  color: var(--kv-white) !important;
  border: none !important;
  padding: 12px 30px !important;
  border-radius: 30px !important;
  font-family: 'Montserrat', sans-serif !important;
  font-weight: 600 !important;
  font-size: 1em !important;
  cursor: pointer !important;
  transition: all 0.3s ease !important;
  text-decoration: none !important;
  display: inline-block !important;
}
.button:hover {
  background-color: var(--kv-gold-dark) !important;
  transform: translateY(-2px) !important;
  box-shadow: 0 4px 12px rgba(245, 166, 35, 0.4) !important;
}
/* Anchor List Styling */
.anchor-list {
  list-style: none !important;
  padding: 0 !important;
  display: flex !important;
  gap: 15px !important;
  flex-wrap: wrap !important;
}
.anchor-list li {
  margin: 0 !important;
}
.anchor-list li a {
  color: var(--kv-gold-dark) !important;
  font-weight: 600 !important;
  text-decoration: none !important;
  padding: 8px 16px !important;
  border-radius: 20px !important;
  border: 2px solid var(--kv-gold-primary) !important;
  transition: all 0.3s ease !important;
  display: inline-block !important;
}
.anchor-list li a:hover {
  background-color: var(--kv-gold-primary) !important;
  color: var(--kv-white) !important;
}
/* ========================================
   TABLES
   ======================================== */
table {
  border-collapse: collapse !important;
  width: 100% !important;
  background-color: var(--kv-white) !important;
}
table thead {
  background: linear-gradient(135deg, var(--kv-gold-primary) 0%, var(--kv-orange-accent) 100%) !important;
  color: var(--kv-white) !important;
}
table thead th {
  padding: 12px !important;
  font-weight: 600 !important;
  text-align: left !important;
  border: none !important;
}
table tbody tr {
  border-bottom: 1px solid var(--kv-grey-light) !important;
}
table tbody tr:nth-child(odd) {
  background-color: var(--kv-warm-white) !important;
}
table tbody tr:hover {
  background-color: var(--kv-light-bg) !important;
}
table td {
  padding: 10px 12px !important;
  color: var(--kv-text-primary) !important;
}
/* ========================================
   CODE BLOCKS
   ======================================== */
pre,
code,
.syntax {
  background-color: var(--kv-warm-white) !important;
  border-left: 3px solid var(--kv-gold-primary) !important;
  padding: 10px !important;
  font-family: 'Courier New', monospace !important;
  color: var(--kv-charcoal) !important;
  border-radius: 4px !important;
  display: inline-block !important;
  max-width: 100% !important;
  word-wrap: break-word !important;
}
.syntax {
  display: inline !important;
  padding: 2px 6px !important;
}
/* ========================================
   FOOTER / CTA BOTTOM
   ======================================== */
#cta-bottom {
  background: linear-gradient(135deg, var(--kv-charcoal) 0%, var(--kv-charcoal-light) 100%) !important;
  color: var(--kv-white) !important;
  padding: 40px !important;
  text-align: center !important;
}
#cta-bottom a {
  color: var(--kv-white) !important;
  text-decoration: underline !important;
  font-weight: 600 !important;
}
#cta-bottom a:hover {
  color: var(--kv-gold-light) !important;
}
/* ========================================
   UTILITY CLASSES
   ======================================== */
.border-top-gap {
  border-top: 1px solid var(--kv-grey-light) !important;
  margin-top: 20px !important;
  padding-top: 20px !important;
}
/* ========================================
   RESPONSIVE ADJUSTMENTS
   ======================================== */
@media (max-width: 768px) {
  .navigation nav ul {
    flex-direction: column !important;
    gap: 10px !important;
  }

  .navigation nav ul li {
    width: 100% !important;
  }

  .navigation nav ul li a {
    display: block !important;
    text-align: center !important;
  }

  .horizontal-nav h2 {
    font-size: 1.4em !important;
  }

  .row.amethyst.no-toc h1 {
    font-size: 2em !important;
  }

  /* Mobile description list */
  .c-description-list__group {
    grid-template-columns: 1fr !important;
    gap: 5px !important;
  }

  .c-description-list__term {
    padding-bottom: 5px !important;
  }
}
/* ========================================
   CUSTOM KOLIBRI VISION ACCENTS
   ======================================== */
/* Gradient Underline Effect */
.kv-gradient-underline::after {
  content: '';
  display: block;
  width: 60px;
  height: 3px;
  background: linear-gradient(90deg, var(--kv-gold-primary) 0%, var(--kv-orange-accent) 100%);
  margin-top: 10px;
}
